<?phpclass alertctl extends control{	function handle_alert_data($args){		global $status;		$rst = $_REQUEST;		$rst['time'] = trim($rst['time']);		$rst['time'] = str_replace(array('：','－','　'),array(':','-',' '),$rst['time']);		if(strpos($rst['time'],' ')===false){			$rst['time'] = date('Y-m-d ') . $rst['time'];		}		//echo $rst['time'];die;		$rst['userid'] = $_SESSION['id'];		$status = 'data_ok';		return $rst;	}		function get_del_alert($args){		global $status;		$id = intval($_REQUEST['id']);		$userid = intval($_SESSION['id']);		if($id>0 and $userid>0){			$status = 'got_id';			return array('id'=>$id, 'userid'=>$userid);		}else{			$status = 'data_lack';			return;		}	}}?>